import {useState, useEffect} from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [itemText, setItemText] = useState('');
  const [itemsList, setItemsList] = useState([]);

  let itemlocation = 'http://localhost:5500/api/item';
  // add new todo item 
  const addItem = async (e) => {
    e.preventDefault();

    try{
      const res = await axios.post(itemlocation, {item: itemText});
      console.log(res);
      setItemText('');
    }
    catch(err){
      console.log(err);
    }
  }

  let itemslocation = 'http://localhost:5500/api/items';
  // get all todo items
  useEffect(() => {
    const getItemsList = async () => {
      try{
        const res = await axios.get(itemslocation);
        console.log(res);
      }catch(err){
        console.log(err);
      }
    }
    getItemsList(); 
  }, [])



  return (
    <div className="App">
      <form className='form' onSubmit={e => addItem(e)}>
        <input type = "text" placeholder='Add Todo Item' onChange={e => {setItemText(e.target.value)}} value = {itemText}/>

        <button type = "submit">Add</button>
      </form>
      <div className='todo-listItems'>
         <div className='todo-item'>
          <p classNmae = "item-content">this is the item 1</p>
          <button className='update-item'>Update</button>
          <button className= "delete-item">Delete</button>
         </div>
         <div className='todo-item'>
          <p classNmae = "item-content">this is the item 1</p>
          <button className='update-item'>Update</button>
          <button className= "delete-item">Delete</button>
         </div>
         <div className='todo-item'>
          <p classNmae = "item-content">this is the item 1</p>
          <button className='update-item'>Update</button>
          <button className= "delete-item">Delete</button>
         </div>
      </div>
    </div>
  );
}

export default App;
